import React, {useEffect, useState} from 'react'
import axios from 'axios'
import logo from './logo.svg';
import './App.css';

function App() {

  const urlbase = 'http://127.0.0.1:5000/'
  const urlComparate = urlbase + 'comparator/'
  const urlDelete = urlbase + 'delete/'

  const [comparators, setComparators] = useState()

  const onDelete =async (id, index)=>{
    // await axios.delete(`${urlDelete}${id}`).then(() => location.reload)
    setComparators((prevState)=>{
      const algo=  prevState.splice(index,1)
      console.log(algo)
      //prevState.splice(index,1)
      console.log("prevstate", prevState)
      console.log("index",index)
      console.log("id",id)
      const newComparator = [...prevState]
      return newComparator
    })
  }
  console.log(comparators)


  const fechApi = async () => {
    await axios.get(urlComparate).then((response) => {setComparators(response.data.comparatorList)})
  }

   useEffect(() => {
    fechApi()
   },[])

  return (
    <div className="App">
      <table>
        <thead>
        <tr>
          <th>Product Name</th>
          <th>Precio Nuevo</th>
          <th>Precio Promedio</th>
          <th>Precio M√°s bajo</th>
          <th>Alerta Umbral</th>
          <th>Alerta Historico</th>
          <th>despublicar</th>
        </tr>
        </thead>
        <tbody>
        { !comparators ? 'Cargando...' : 
            comparators.map( (comparator,index)=>{ 
              return <tr key={comparator.id}>
                        {/* <td>{comparator.umbral_flag ? comparator.product_name : 
                        (comparator.best_historical_flag ? comparator.product_name : 
                        comparator.product_name)}</td>  */}
                        <td>
                          {(comparator.umbral_flag || comparator.best_historical_flag) && (comparator.price_newest != '0')? 
                            <b>{comparator.product_name}</b>: comparator.product_name}
                        </td>
                        <td>
                          {comparator.price_newest == '0' ?
                            'sin stock' :  
                            comparator.price_newest}
                        </td> 
                        <td>
                          {comparator.price_average}
                        </td> 
                        <td>
                          {comparator.price_best}
                        </td>
                        <td>{comparator.umbral_flag && comparator.price_newest != '0' ? 
                            'üö®' : 
                            'No'}
                        </td>
                        <td>{comparator.best_historical_flag && comparator.price_newest != '0' ? 'üö®' : 'No'}</td>
                        <td onClick={()=>onDelete(comparator.id, index)}>
                          <p>üóëÔ∏è</p>                            
                        </td>
                    </tr>
            })
          }
      </tbody>
      </table>

    </div>
  );
}

export default App;
