import React, {useEffect, useState} from 'react'
import axios from 'axios'
import logo from './logo.svg';
import './App.css';

function App() {

  const url = 'http://127.0.0.1:5000/comparator/'

  const [comparators, setComparators] = useState()

  const fechApi = async () => {
    await axios.get(url).then((response) => {setComparators(response.data.comparatorList)})
  }

   useEffect(() => {
    fechApi()
   },[])

  return (
    <div className="App">
      <table>
        <thead>
        <tr>
          <th>Product Name</th>
          <th>Precio Nuevo</th>
          <th>Precio Promedio</th>
          <th>Precio MÃ¡s bajo</th>
          <th>Alerta Umbral</th>
          <th>Alerta Historico</th>
        </tr>
        </thead>
        <tbody>
        { !comparators ? 'Cargando...' : 
            comparators.map( (comparator,index)=>{ 
              return <tr key={index}>
                        <td>{comparator.umbral_flag ? '<b>'+comparator.product_name+'</b>' : (comparator.best_historical_flag ? comparator.product_name : '')}</td>
                        <td>{comparator.price_newest == '0' ? 'sin stock' : comparator.price_newest}</td> 
                        <td>{comparator.price_average}</td> 
                        <td>{comparator.price_best}</td>
                        <td>{comparator.umbral_flag ? 'ðŸš¨' : 'No'}</td>
                        <td>{comparator.best_historical_flag ? 'ðŸš¨' : 'No'}</td>
                    </tr>
            })
          }
</tbody>
      </table>

    </div>
  );
}

export default App;
